sys_script_include.script:
 // The first line creates a variable called SimpleADD and executes the class.create() function, This starts the definition of the object

var SimpleAdd = Class.create();

// Its where the prototype is edited in order to make the class actuall do some thing

SimpleAdd.prototype = {
	// Initialize is automataically called whe n object is created 

    initialize: function(n) {
		gs.info('Creating new object');
		this.number = n-0 || 0;
    },

	increment : function(){

		this.number++;
		gs.info(this.number);
		return this;



	},




    type: 'SimpleAdd'
};
